insert into class (num, name, credits, description, department_id) values('127', 'Applied Music: Commercial Voice I', 1, 'These courses provide instruction in voice or instrument for students who wish to pursue a music program on an individualized basis intended for solo performance, or for students who wish to transfer to a program leading to a degree in music, or for other students with musical background and interest. Students may choose either from the traditional repertoire or from a jazz/rock/show repertoire. One half-hour lesson and seven hours of practice per week. Course fee.', 38);
