insert into class (num, name, credits, description, department_id) values('167', 'Applied Music: Composition I', 2, 'This course provides individualized instruction in vocal/ instrumental composition and arranging for students who wish to pursue a music degree, or for those with musical background and interest. One-half hour lesson and seven hours of work/practice per week. Prerequisite: permission of instructor. Course fee.', 38);
