insert into class (num, name, credits, description, department_id) values('179', 'Applied Music: Keyboard Theory I', 1, 'This course is designed to develop specific theoretical skills to be realized on the keyboard. Subjects include all varieties of chords, figured bass realization, transposition, and scales. Total contact hours per week: 30 minute individual lesson is scheduled biweekly with the instructor per regular semester. An additional 3 1/2 hours of practice per week is expected. Prerequisite: MUS 104 or corequisite and MUS 115, or permission of instructor. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('185', 'Classical Guitar Ensemble I', 1, 'This course provides an ensemble experience by giving the guitar student an opportunity to perform in a group environment along with guitarists. Students are exposed to a variety of classical music literature, as well as styles and techniques associated with classical music, with an emphasis on guitar. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('186', 'Classical Guitar Ensemble II', 1, 'This course provides an ensemble experience by giving the guitar student an opportunity to perform in a group environment along with guitarists. Students are exposed to a variety of classical music literature, as well as styles and techniques associated with classical music, with an emphasis on guitar. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('187', 'Chamber Music Ensemble I', 1, 'Students participating in this course have the opportunity to play a wide variety of classical music styles in a chamber setting, allowing for a high level of musical interaction within the ensemble. Students gain practice in basic musicianship, intonation, tone, color and sensitivity. Students develop instrumental techniques and participate in concerts, theater productions and other appearances as scheduled. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('188', 'Chamber Music Ensemble II', 1, 'Students participating in this course have the opportunity to play a wide variety of classical music styles in a chamber setting, allowing for a high level of musical interaction within the ensemble. Students gain practice in basic musicianship, intonation, tone, color and sensitivity. Students develop instrumental techniques and participate in concerts, theater productions and other appearances as scheduled. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Course fee.', 38);
