insert into class (num, name, credits, description, department_id) values('193', 'Independent Study Music', 3, 'A course of instruction for students who wish to pursue a music program on an individualized basis intended for solo performances or for students who wish to transfer to a program leading to a degree in music, or other students with musical background and interest.', 38);
insert into class (num, name, credits, description, department_id) values('194', 'Independent Study: Music', 4, 'Course description not available at this time.', 38);
insert into class (num, name, credits, description, department_id) values('201', 'The Art of Listening I (GH) (D)', 3, 'Art of Listening I is an introductory study of music styles, media and forms as they exist in our culture from ancient times to the early 19th century through a survey of standard concert repertory and its historical development. Special emphasis is placed on aural identification. This course may require field trips.', 38);
insert into class (num, name, credits, description, department_id) values('202', 'The Art of Listening II (GH) (D)', 3, 'Art of Listening II is an introductory study of music styles, media and forms as they exist in our culture from the early 19th century to the present through a survey of standard concert repertory and its historical development. Special emphasis is placed on aural identification. This course may require field trips.', 38);
insert into class (num, name, credits, description, department_id) values('203', 'Music Theory III', 4, 'Music Theory III is a study of chromatic harmony highlighting stylistic differences between 18th and 19th century practices. Original composition is encouraged. The course includes sight-singing, dictation, rhythmic drills, and the study of musical forms. Course includes 45 hours of lecture and 30 hours of lab per semester. Prerequisite: MUS 104 or equivalent. Usually offered in fall semester.', 38);
insert into class (num, name, credits, description, department_id) values('204', 'Music Theory IV', 4, 'Music Theory IV is a study of advanced chromatic harmony and 20th century compositional techniques. Original compositions are required. Includes sight-singing, dictation and keyboard exercises; rhythmic drills; and study of music forms. Course includes 45 hours of lecture and 30 hours of lab per semester. Prerequisite: MUS 203 or equivalent. Usually offered in spring semester.', 38);
insert into class (num, name, credits, description, department_id) values('205', 'Chorus III', 1, 'The chorus performs both sacred and secular works from the 16th century to the present. Vocal problems are given attention. Members are expected to participate in concerts, Broadway-type productions, television appearances, etc., as scheduled by the director. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisites: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('206', 'Chorus IV', 1, 'The chorus performs both sacred and secular works from the 16th century to the present. Vocal problems are given attention. Members are expected to participate in concerts, Broadway-type productions, television appearances, etc., as scheduled by the director. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisites: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('207', 'A Cappella Singers III', 1, 'The A Cappella Singers perform unaccompanied vocal music from the Renaissance to the present. Students develop vocal techniques. Participation in concerts, community events, and other appearances, as scheduled, is required. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisites: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('208', 'A Cappella Singers IV', 1, 'The A Cappella Singers perform unaccompanied vocal music from the Renaissance to the present. Students develop vocal techniques. Participation in concerts, community events, and other appearances, as scheduled, is required. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisites: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('209', 'Band III', 1, 'The band provides practice in basic musicianship, intonation and tone color. It is a study of various types and styles of music written or arranged for band. Students are prepared for public performances. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisites: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('210', 'Band IV', 1, 'The band provides practice in basic musicianship, intonation and tone color. It is a study of various types and styles of music written or arranged for band. Students are prepared for public performances. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisites: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('211', 'Jazz Ensemble III', 1, 'The HCC Jazz Ensemble performs instrumental jazz from the earliest to contemporary forms. Students develop instrumental techniques and participate in concerts, theater productions and other appearances, as scheduled. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisites: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('212', 'Jazz Ensemble IV', 1, 'The HCC Jazz Ensemble performs instrumental jazz from the earliest to contemporary forms. Students develop instrumental techniques and participate in concerts, theater productions and other appearances, as scheduled. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisites: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('213', 'Percussion Ensemble III', 1, 'This course consists of the performance of percussion repertoire and the development of techniques on various percussion instruments. Participation in concerts, theater productions and other appearances, as scheduled, is required. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisites: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('214', 'Percussion Ensemble IV', 1, 'This course consists of the performance of percussion repertoire and the development of techniques on various percussion instruments. Participation in concerts, theater productions and other appearances, as scheduled, is required. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisites: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('215', 'Introduction to Electronic Music', 3, 'Students are able to create interesting electronic musical compositions through introductory studies of notation and sequencing software programs. Projects include computer-generated scores and compositions generated with MIDI, digital audio and digital synthesis. Course includes 30 hours of lecture and 30 hours of lab per semester. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('216', 'World Music (GH) (D)', 3, 'World Music has a broad scope within the realm of the music from other cultures, with the emphasis on music from cultures other than Western European. Students consider a variety of works from a variety of cultures across the globe by representative performers and composers. This course may require field trips.', 38);
insert into class (num, name, credits, description, department_id) values('218', 'Vocal Jazz Ensemble III', 1, '"Second Shift" performs vocal jazz from the earliest to contemporary forms. Students develop vocal jazz techniques. Participation is required in concerts, theater productions and other appearances, as scheduled. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisite: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('219', 'Vocal Jazz Ensemble IV', 1, '"Second Shift" performs vocal jazz from the earliest to contemporary forms. Students develop vocal jazz techniques. Participation is required in concerts, theater productions and other appearances, as scheduled. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisite: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('220', 'Orchestra III', 1, 'The orchestra performs the great orchestral masterpieces as well as new orchestral literature. Students gain practice in basic musicianship, intonation, tone, color and sensitivity. Students are prepared for public performance. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisite: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('221', 'Orchestra IV', 1, 'The orchestra performs the great orchestral masterpieces as well as new orchestral literature. Students gain practice in basic musicianship, intonation, tone, color and sensitivity. Students are prepared for public performance. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisite: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('222', 'Popular Music of the United States (GH) (D)', 3, 'The history of popular music in the United States is studied from the standpoint of cultural impact. This course includes the study of the origins of a "popular" audience, through the late 19th century; the development of Jazz, Ragtime, Blues, Tin-Pan-Alley, show music, Swing, motion picture music, Country, protest music, Rock and Roll, Folk and Rock. Students are required to participate in listening assignments representative of the various stages of the development of popular music. This course may require field trips.', 38);
insert into class (num, name, credits, description, department_id) values('223', 'Evolution of Jazz (GH) (D)', 3, 'Evolution of Jazz is a general introductory course exploring the history and development of jazz music in the United States over its century-long history and from its African and American precursors to its present-day practice throughout the world. The basic structural elements of music are introduced to provide a foundation for critical listening and discussion. This course may require field trips.', 38);
insert into class (num, name, credits, description, department_id) values('224', 'Vocal Performance Workshop III', 1, 'This course provides performance opportunities for students who wish to pursue a music program intended for performance, or other students with musical interest. Students collaboratively learn musical scenes and become comfortable with a professional rehearsal process, culminating in a public performance. The instructor provides feedback to aid students'' development of complete dramatic material. Assigned scenes include selections from the classical, operatic, or musical theater repertoire. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisite: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('225', 'Vocal Performance Workshop IV', 1, 'This course provides performance opportunities for students who wish to pursue a music program intended for performance, or other students with musical interest. Students collaboratively learn musical scenes and become comfortable with a professional rehearsal process, culminating in a public performance. The instructor provides feedback to aid students'' development of complete dramatic material. Assigned scenes include selections from the classical, operatic, or musical theater repertoire. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Prerequisite: permission of instructor and/or an audition. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('227', 'Applied Music: Commercial Voice V', 1, 'These courses provide instruction in voice or instrument for students who wish to pursue a music program on an individualized basis intended for solo performance, or for students who wish to transfer to a program leading to a degree in music, or for other students with musical background and interest. Students may choose either from the traditional repertoire or from a jazz/rock/show repertoire. One half-hour lesson and seven hours of practice per week. Course fee.', 38);
