insert into class (num, name, credits, description, department_id) values('275', 'Applied Music Keyboard Theory', 1, 'This course is designed to develop specific theoretical skills to be realized on the keyboard. Subjects include all varieties of chords, figured bass realization, transposition, and scales. Total contact hours per week: 30 minutes individual lesson is scheduled weekly with the instructor per regular semester. An additional 7 hours of practice per week is expected. Course fee. Prerequisite: permission of instructor.', 38);
insert into class (num, name, credits, description, department_id) values('279', 'Applied Music: Keyboard Theory II', 1, 'This course is a continuation of MUS 179 and is designed to develop specific theoretical skills to be realized on the keyboard. Subjects include all varieties of chords, figured bass realization, transposition, choral score reading and scales. Total contact hours per week: 30 minutes individual lesson is scheduled biweekly with the instructor per regular semester. An additional 3 1/2 hours of practice per week is expected. Prerequisite: MUS 179. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('285', 'Classical Guitar Ensemble III', 1, 'This course provides an ensemble experience by giving the guitar student an opportunity to perform in a group environment along with guitarists. Students are exposed to a variety of classical music literature, as well as styles and techniques associated with classical music, with an emphasis on guitar. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('286', 'Classical Guitar Ensemble IV', 1, 'This course provides an ensemble experience by giving the guitar student an opportunity to perform in a group environment along with guitarists. Students are exposed to a variety of classical music literature, as well as styles and techniques associated with classical music, with an emphasis on guitar. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('287', 'Chamber Music Ensemble III', 1, 'Students participating in this course have the opportunity to play a wide variety of classical music styles in a chamber setting, allowing for a high level of musical interaction within the ensemble. Students gain practice in basic musicianship, intonation, tone, color and sensitivity. Students develop instrumental techniques and participate in concerts, theater productions and other appearances as scheduled. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('288', 'Chamber Music Ensemble IV', 1, 'Students participating in this course have the opportunity to play a wide variety of classical music styles in a chamber setting, allowing for a high level of musical interaction within the ensemble. Students gain practice in basic musicianship, intonation, tone, color and sensitivity. Students develop instrumental techniques and participate in concerts, theater productions and other appearances as scheduled. Course includes 15 hours of lecture and 30 hours of rehearsal per semester. Course fee.', 38);
insert into class (num, name, credits, description, department_id) values('101', 'Fundamentals of Nursing', 7, 'In this course, students utilize the nursing process to contribute to a plan of care to meet the assessed basic health and wellness needs of clients. Course includes 60 hours of lecture, 45 hours of laboratory, and 67.5 hours in a clinical setting per semester. Prerequisites: admission to the Nursing Program, current CPR Certification for Healthcare Providers, BIO 203, ENG 101 and PSY 101. Corequisites: BIO 204 & PSY 214. Course fee.', 39);
insert into class (num, name, credits, description, department_id) values('105', 'Medical-Surgical Nursing 1', 4, 'In this course, students utilize the nursing process to develop a plan of care to meet the assessed health and wellness needs of clients with commonly occurring health issues. Course includes 30 hours of lecture and 112 hours in a clinical setting. Prerequisites: current CPR Certification for Healthcare Providers, NURS 101, BIO 204, PSY 214. BIO 205 is a prerequisite for NURS 210, NURS 220, and NURS 221. Course fee.', 39);
insert into class (num, name, credits, description, department_id) values('108', 'Mental Health Nursing', 3, 'In this course, students utilize the nursing process to apply mental health concepts to clients with commonly occurring mental health issues. Course includes 30 lecture hours and 67.5 laboratory hours and is offered in the Spring and Fall semesters. Prerequisites: Current CPR Certification for Health Care Providers, NURS 101, BIO 204, PSY 214. Course fee.', 39);
insert into class (num, name, credits, description, department_id) values('110', 'Mental Health Nursing', 3, 'Students use the nursing process to apply mental health concepts to clients with commonly occurring mental health issues and to plan care for clients with mental health disorders, with consideration for ethnic and cultural variations in beliefs, values, and health practices. Health promotion and maintenance are emphasized. Students begin to develop a foundation for practice through review of clinical literature and are challenged to reflect on their own behaviors and methods of communication. Course includes 30 lecture hours and 45 laboratory hours and is offered in the Spring and Fall semesters. Prerequisites: Current CPR Certification for Health Care Providers, NURS 101, BIO 204, PSY 214. Course fee.', 39);
insert into class (num, name, credits, description, department_id) values('112', 'Contemporary Issues in Nursing', 1, 'In this course, students gain insight into the impact of contemporary issues on professional nursing practice. Students discover how current trends in nursing affect their personal practice as registered nurses and contribute to the overall health care of our nation. Course includes 15 hours of lecture. Prerequisites: Current CPR Certification for Healthcare Providers, NURS 101, PSY 214, BIO 204.', 39);
insert into class (num, name, credits, description, department_id) values('192', 'Independent Study: Nursing', 2, 'Course description not available at this time.', 39);
insert into class (num, name, credits, description, department_id) values('208', 'Maternal-Child Nursing', 5, 'Course description not available at this time.', 39);
insert into class (num, name, credits, description, department_id) values('210', 'Medical-Surgical Nursing II', 4, 'In this course, students utilize the nursing process to develop and/or adapt a plan of care to meet the assessed health and wellness needs of clientswith complex health care issues. Seven weeks. Course includes 37.5 hours of lecture and 90 hours in a clinical setting per 7-week term. Prerequisites: Current CPR Certification for Healthcare Providers, NURS 105, NURS 108, NURS 112, BIO 205. Course fee.', 39);
insert into class (num, name, credits, description, department_id) values('211', 'Medical-Surgical Nursing III', 9, 'In this course, students utilize the nursing process to create and coordinate a plan of care for meeting the assessed health and wellness needs of clients with multiple and/or complex health care issues. Course includes 60 hours of lecture and 225 hours in a clinical setting per semester. Prerequisites: Current CPR Certification for Healthcare Providers, NURS 210, NURS 212, NURS 220, and NURS 221. Course fee.', 39);
insert into class (num, name, credits, description, department_id) values('212', 'Leadership in Nursing', 1, 'In this course, students prepare to function in the role of a registered professional nurse. The concepts of leadership and management are presented with corresponding clinical applications in Medical-Surgical Nursing II (NURS 210). Course includes 15 hours of lecture. Prerequisites: NURS 105, NURS 107, and NURS 112.', 39);
insert into class (num, name, credits, description, department_id) values('220', 'Maternal-Newborn Nursing', 3, 'Students utilize the nursing process to develop and/or adapt a plan of care to meet the assessed health and wellness needs of maternity and newborn clients. Course includes 30 hours of lecture and 45 hours in a clinical setting and is offered in the Spring and Fall semesters. Prerequisites: current CPR Certification for Healthcare Providers, NURS 105, NURS 108, NURS 112, and BIO 205. Course fee.', 39);
insert into class (num, name, credits, description, department_id) values('221', 'Pediatric Nursing', 2, 'Students utilize the nursing process to develop and/or adapt a plan of care to meet the assessed health and wellness needs of the pediatric patient and family. This course includes 22.5 hours of lecture and 45 hours in a clinical setting and is offered in the Spring and Fall semesters. Prerequisites: current CPR Certification for Healthcare Providers, NURS 105, NURS 108, NURS 112, and BIO 205. Course fee.', 39);
insert into class (num, name, credits, description, department_id) values('230', 'Pediatric Nursing', 3, 'Students utilize the nursing process to develop and/or adapt a plan of care to meet the assessed health and wellness needs of the pediatric patient and family. This course includes 22.5 hours of lecture and 45 hours in a clinical setting and is offered in the Spring and Fall semesters. Prerequisites: current CPR Certification for Healthcare Providers, NURS 105, NURS 108, NURS 112 and BIO 205. Course fee.', 39);
insert into class (num, name, credits, description, department_id) values('100', 'Keyboarding Basics', 1, 'Keyboarding Basics teaches students to operate the computer keyboard by touch and begin development of acceptable speed and accuracy levels. Recommended for students who have not had formal keyboarding instruction or as a refresher for students returning to the work force in an office setting. Credit for prior learning is available. Course fee.', 40);
insert into class (num, name, credits, description, department_id) values('113', 'Intermediate Keyboarding and Document Processing', 3, 'This course emphasizes the development of speed and accuracy with alphabetic, numeric and symbolic keyboarding and proofreading techniques. Assignments are customized to the individual student''s skill level. Students use word processing software as a tool to prepare various business letters, memos, tables and reports. Decision-making skills are used to select document formats. Course projects emphasize the application of written communication skills and the ability to produce quality documents efficiently. Prerequisites: OS 100 or credit by assessment. Course fee.', 40);
insert into class (num, name, credits, description, department_id) values('116', 'Communication Technologies', 4, 'This course introduces students to the fast-paced world of end-user communication technology and telecommunications. Students acquire knowledge of the fundamentals of electronic mail, voice processing, teleconferencing, wireless communication, and related technologies. The course includes student use of speech recognition software. Course fee.', 40);
insert into class (num, name, credits, description, department_id) values('129', 'Introduction to Office Procedures and Management', 4, 'This course provides students with opportunities to explore various office careers for development of short- and long-term career goals. Key topics include: written and oral business communication skills; international etiquette; records/financial management/office design, space and workplace safety; minute-taking; and an introduction to supervision. Hands-on practice in the use of various filing systems, event planning/travel arrangements as well as case studies and role-playing will provide students with experience in real-world business practices. Course fee.', 40);
insert into class (num, name, credits, description, department_id) values('135', 'Medical Office Systems', 3, 'Office administration in a medical setting requires human relations skills, confidentiality and computer software proficiency. The role and responsibilities of the medical office assistant are explored through simulations and application of office systems theory. Topics include the ethical and legal responsibilities of handling patient records, preparation of medical records, billing and insurance forms, professional reports, scheduling and communications. Course fee.', 40);
insert into class (num, name, credits, description, department_id) values('136', 'Introduction to Bookkeeping: Quickbooks', 3, 'This course introduces basic bookkeeping concepts and procedures vital to developing and controlling business activities through the use of accounting software. Accounting for payables and receivables, preparing payroll activities and producing financial statements will be major topics of the course. Students will gain proficiency in developing sample databases designed to simulate real-world bookkeeping functions. Prerequisite: Qualifying score on the math assessment or MATH 021. Course fee.', 40);
insert into class (num, name, credits, description, department_id) values('140', 'Fundamentals of Healthcare', 3, 'This course focuses on the knowledge and skills common to all healthcare occupations. This course will explore content which includes healthcare delivery systems, communication skills, legal and ethical responsibilities, wellness and disease concepts, infection control, CPR, introductory first aid and safety, employability, computer skills, and professionalism common to health care occupations. Course fee.', 40);
insert into class (num, name, credits, description, department_id) values('214', 'Advanced Keyboarding & Document Processing', 3, 'This course emphasizes the continued development of speed and accuracy with keyboarding exercises. Assignments are customized to the individual student''s skill level. Students use word processing software as a tool to prepare various business documents. Decision-making skills are used to select document formats. Course projects emphasize advanced word processing skills such as advanced tables, merged documents, tracking changes and macros. Prerequisite: OS 113. Course fee.', 40);
insert into class (num, name, credits, description, department_id) values('235', 'Medical Transcription', 3, 'Students master transcription techniques and apply medical terminology and English language usage to prepare medical documents. Documents illustrate appropriate medical report forms, use of reference materials and computer software. Knowledge of software productivity techniques is essential for successful transcription. Prerequisites: AHS 101, ENG 012 or acceptable score on English assessment, and OS 113. Course fee.', 40);
insert into class (num, name, credits, description, department_id) values('245', 'End User Technology Solutions', 3, 'This course provides a comprehensive overview of technology solutions for the organizational end user environment. Emphasis is on the development of business applications using several software packages, including desktop publishing and project management. Prerequisite: OS 116. Course fee.', 40);
insert into class (num, name, credits, description, department_id) values('272', 'Cooperative Education II: Office Systems', 2, 'Course description not available at this time.', 40);
insert into class (num, name, credits, description, department_id) values('273', 'Cooperative Education III: Office Systems', 3, 'Cooperative Education experiences are work-based learning experiences with an employer for a specific period of time. The experience may be paid and must be related to the career and specific curriculum in which the student is enrolled. It is an opportunity for the student to supplement/integrate classroom learning with learning from a related work setting. A student registers for one to four credits of Cooperative Education in the curriculum in which he/she is enrolled. Prerequisite: approval of program coordinator.', 40);
insert into class (num, name, credits, description, department_id) values('002', 'iCanSucceed', 0, 'iCanSucceed is a half day empowerment program. Participants will hear from graduate and current students about how to navigate the academic and social demands of college. Keynote speaker: Dr. LaMarr Darnell Shields. Session will be held Friday, September 9, 2016 in Darlington Hall, Room 202.', 41);
insert into class (num, name, credits, description, department_id) values('003', 'January Orientation', 0, 'For adult learners new to Harford Community College, this program will help you to become acquainted with life on campus and receive information that will prepare you for success at HCC. Registration is required. There will be optional sessions that address math anxiety and college technology. A light dinner will be served. Session will be held Thursday, August 25th in the Globe Caf?.', 41);
insert into class (num, name, credits, description, department_id) values('101', 'Introduction to Law (Same course as PS 106) (GB)', 3, 'This course introduces students to basic legal concepts, principles, and procedures. It is designed to provide the student with an understanding of the structure of the U.S. legal system including the role of the judicial, legislative, and executive branches; the history of law in the United States; the role of attorneys, law enforcement and other legal professionals; ethical and professional issues facing legal professionals; basic categories of law; litigation principles; and alternative dispute resolutions.', 42);
insert into class (num, name, credits, description, department_id) values('104', 'Procedural Law and Evidence', 3, 'The constitutional aspects of arrest, search and seizure are considered, together with interrogation and confession, self-incrimination and right to counsel. Students learn rules of evidence as they apply to law enforcement officers in the performance of their investigatory duties and their testimony in court.', 42);
insert into class (num, name, credits, description, department_id) values('105', 'Legal Research', 3, 'This course focuses on the theories, procedures, and resources used for solving legal problems through research. The student becomes familiar with the various legal publications found in a typical law library and learns to use those resources to develop and execute a plan for the solution of legal problems. Students explore electronic research programs. It is recommended that PL 101 be taken prior to or concurrently with Legal Research.', 42);
insert into class (num, name, credits, description, department_id) values('106', 'Legal Writing', 3, 'This course builds on the knowledge students have acquired in Legal Research. The skills developed in that course are applied to the tasks of case analysis and legal writing. Students are exposed to the methods of analyzing and briefing cases and statutes. The principles of technical legal writing are taught and applied in legal correspondence, instruments, office memoranda, pleadings, court memoranda, and appellate briefs. Prerequisites: ENG 101, PL 105, and PL 110.', 42);
insert into class (num, name, credits, description, department_id) values('110', 'Technology in the Legal Profession', 3, 'This course provides an overview of the application of technology in the legal setting. Students learn the basics of computer hardware and software as well as purchase considerations. A major emphasis is placed on legal word processing and its applications in pleadings and correspondence. Telecommunications, computer assisted legal research, Internet, and other technology utilized in the law office are explored. Credit for prior learning is available. Students are required to take this course prior to PL 106.', 42);
insert into class (num, name, credits, description, department_id) values('111', 'Principles of Criminal Law', 3, 'The substantive law is discussed: how and why laws are created with emphasis on specific offenses against persons and property. Also covered: what constitutes a violation of the law and how police must satisfy the legal requirements imposed by the elements of the statutes so that the state may successfully prosecute a criminal case. Landmark U.S. Supreme Court and selected state court cases are studied.', 42);
insert into class (num, name, credits, description, department_id) values('122', 'Torts and Insurance', 3, 'This course covers the areas of unintentional and intentional torts, and torts based on strict liability. Topics include damages, defenses, and the application of insurance law. Emphasis is placed on the academic as well as the practical aspects of a tort practice. Prerequisite: PL 101.', 42);
insert into class (num, name, credits, description, department_id) values('124', 'Civil Litigation and Procedure', 3, 'This course focuses on both the procedural and substantive aspects of civil litigation. The student becomes familiar with Maryland and federal court procedure, structure, personnel, pleadings, discovery, and other topics. In addition, students study the issues of jurisdiction and evidence. Emphasis is placed on the typical litigation tasks that paralegals are assigned including drafting pleadings, investigation, preparing witnesses, and reviewing records. Prerequisite: PL 101.', 42);
insert into class (num, name, credits, description, department_id) values('201', 'Conflict Analysis and Resolution', 3, 'This course focuses on the study of conflict in both interpersonal and professional contexts. Students examine the sources, symptoms, dynamics, and ramifications of conflict. In addition, the current methods of resolving conflict and disputes are closely examined. Students are given the opportunity to employ specific conflict resolution techniques that can be applied in legal, business, multicultural, and personal settings. Emphasis is placed on examining negotiation, mediation, and arbitration. It is recommended that PL 101 be taken prior to or concurrently with Conflict Analysis and Resolution.', 42);
insert into class (num, name, credits, description, department_id) values('202', 'Bankruptcy and Collections Law', 3, 'This course focuses on the paralegal''s duties in the area of bankruptcy and collection law. Students become familiar with procedural as well as substantive bankruptcy law. In addition, students learn about the law of debtor/creditor relations, collection of judgments, secured transactions, and electronic filing.', 42);
insert into class (num, name, credits, description, department_id) values('203', 'Contract Law', 3, 'This course covers basic issues of the common law of contracts and the Uniform Commercial Code. Subjects covered include conditions precedent and subsequent, statute of frauds, offer, acceptance, consideration, breach, and remedies. The differences in these areas of law between the Uniform Commercial Code and the common law will be highlighted. Basic contract drafting will be learned and practiced. It is recommended that PL 101 be taken prior to or concurrently with this course.', 42);
insert into class (num, name, credits, description, department_id) values('204', 'Constitutional Law', 3, 'This course covers the basic concepts of American government and civil rights and liberties as seen from the application of the Constitution of the United States. Landmark decisions regarding judicial review, separation of powers, and the freedoms guaranteed and protected by the Bill of Rights are discussed. Questions of balance of constitutional rights to privacy and national security are covered. Prerequisite: PL 101, PS 106, PS 101 or HIST 103.', 42);
insert into class (num, name, credits, description, department_id) values('205', 'Business Law', 3, 'This course focuses on a study of the Uniform Commercial Code as it applies to negotiable instruments and secured transactions. This course emphasizes agency, business organizations, and employment law. It examines creditors'' rights, bankruptcy, property law (real and personal), and estates.', 42);
insert into class (num, name, credits, description, department_id) values('233', 'Probate Practice and Procedure', 2, 'This course covers the areas of estate planning and probate procedure. The student becomes familiar with the ways an estate can be planned through the use of wills, trusts, and powers of attorney. Medical decisions are discussed through the use of the living will and medical power of attorney. Probate procedure is covered with an emphasis on Maryland procedure. Tax considerations of both estate planning and probate are considered. Prerequisite: PL 101.', 42);
insert into class (num, name, credits, description, department_id) values('238', 'Law Practice and Professional Conduct', 3, 'This course covers the fundamentals of law office management and professional conduct. Subjects include basic principles and structure of management, employment opportunities for paralegals, timekeeping and accounting systems, marketing issues, administrative and substantive systems in the law office and law library, employee and client relations, law practice technology, and paralegal and attorney ethics.', 42);
insert into class (num, name, credits, description, department_id) values('242', 'Real Estate Transactions', 3, 'This course covers issues related to the ownership of real property, real estate finance, and the landlord/tenant relationship. Emphasis is on the practical aspects of real estate and landlord/tenant practice, including settlement, title search, recordation, zoning, lease drafting, and lease enforcement. Prerequisite: PL 101.', 42);
insert into class (num, name, credits, description, department_id) values('244', 'Family Law and Litigation', 2, 'This course covers the rights and responsibilities of parents, children, and spouses in the context of the family. Topics include the traditional areas of divorce, custody, support, and adoption. Newer areas such as pre-nuptial agreements, surrogacy, the legal rights of women, and divorce mediation are also considered. Litigation issues related to all topics will be addressed. Prerequisites: PL 101 and PL 124.', 42);
insert into class (num, name, credits, description, department_id) values('245', 'Legal Environment of Business', 3, 'The student is introduced to the legal environment in which businesses operate. The course covers sources of law and the application of law to business. Areas examined include business crimes, contracts (under common law and the Uniform Commercial Code), sales torts (including product liability), administrative, antitrust, environmental, and consumer protection laws.', 42);
insert into class (num, name, credits, description, department_id) values('246', 'Administrative Law and Procedure', 3, 'This course provides an overview of the functions and procedures of federal and state administrative law. Special focus is placed on using the Internet and legal research in various state and federal agencies. Topics include the Administrative Procedure Act, rulemaking, hearing procedure, state and federal applications, privacy issues, and the Freedom of Information Act. Prerequisite: PL 124.', 42);
insert into class (num, name, credits, description, department_id) values('248', 'Internship in Paralegal Studies', 3, 'The internship is designed to provide students with experiences typical of those encountered by paralegals in the work setting. Students work 135 hours as interns in law offices, governmental agencies, or the court system and spend 5 hours in the classroom. Prerequisites: PL 101, PL 105, PL 106, PL 124. Permission of the coordinator is required.', 42);
insert into class (num, name, credits, description, department_id) values('273', 'Cooperative Education III: Paralegal', 3, 'Cooperative Education experiences are work-based learning experiences with an employer for a specific period of time. The experience may be paid and must be related to the career and specific curriculum in which the student is enrolled. It is an opportunity for the student to supplement/integrate classroom learning with learning from a related work setting. A student registers for one to four credits of Cooperative Education in the curriculum in which he/she is enrolled. Prerequisites: PL 101, PL 105, PL 106, PL 124. Permission of the program coordinator is required.', 42);
insert into class (num, name, credits, description, department_id) values('101', 'Introduction to Philosophy (GH)', 3, 'This course is a study of some of the major questions and issues arising in philosophy. Course content includes selected philosophers'' views on the nature of knowledge, the existence of God, ethical values and the role of the State.', 43);
insert into class (num, name, credits, description, department_id) values('200', 'Principles of Logic (GH)', 3, 'Students examine selected principles of formal and informal logic with the purpose of developing the ability to think critically, reason clearly and use language precisely. The course provides students with theoretical and practical reasoning skills needed to construct sound arguments and evaluate the arguments of others.', 43);
insert into class (num, name, credits, description, department_id) values('205', 'Ethics (GH)', 3, 'This introductory course in philosophical ethics encompasses the prominent ethical theories of Western philosophy and considers the application of ethics to modern cases and current situations. This course acquaints students with the major philosophical ethical thinkers in Western philosophy and their ethical theories. It gives students the philosophical perspectives and skills needed to recognize, understand, and apply these theories to contemporary issues in an intelligent and effective way by applying the theories to ethical cases.', 43);
insert into class (num, name, credits, description, department_id) values('220', 'Bioethics (GH)', 3, 'The course covers pertinent ethical theories and applies them to cases drawn from several health care fields. Students gain philosophical understanding and ethical techniques necessary to identify and deal with such issues in theory and practice.', 43);
insert into class (num, name, credits, description, department_id) values('221', 'Business Ethics (GH)', 3, 'This course serves as an introduction to the philosophical study of ethics and the applied sub discipline of business ethics by covering pertinent ethical theories and applies them to cases drawn from the several business ethics fields. Students will gain philosophical understanding and ethical techniques necessary to identify and deal with such issues in theory and practice.', 43);
insert into class (num, name, credits, description, department_id) values('222', 'Environmental Ethics', 3, 'This course examines both the theory and practice of the relationship of humans to the non-human world. In addition, students explore environmental thinking with an historical context. Among issues discussed are population and consumption, food ethics, animal rights, and climate control.', 43);
insert into class (num, name, credits, description, department_id) values('250', 'Philosophy of Religion (GH)', 3, 'This course addresses prominent questions generated by a philosophical study of religions. As such, the method is rational evaluation and fair-minded scrutiny of the issues. Issues include: the existence and nature of god, the afterlife, the soul, and pluralism. The course also examines insights and understandings proposed by some of the greatest thinkers in the discipline. The course is an objective academic study and is not faith-based.', 43);
insert into class (num, name, credits, description, department_id) values('101', 'Black & White Photography I', 3, 'Black and White Photography I is an introductory course in traditional film-based photography, teaching 35mm camera operation and wet darkroom techniques. Course includes 30 hours of lecture and 30 hours of lab per semester. No previous photography experience is required. A 35mm film camera capable of manual exposure operation is required. Course fee.', 44);
insert into class (num, name, credits, description, department_id) values('102', 'Black & White Photography II', 3, 'Black and White Photography II is an advanced course in photographic techniques, styles and aesthetics. The refinement of camera exposure techniques, negative quality and print quality is emphasized. Course includes 30 hours of lecture and 30 hours of lab per semester. Prerequisite: PHOT 101 or permission of instructor. A 35mm film camera capable of manual exposure operation is required. Course fee.', 44);
insert into class (num, name, credits, description, department_id) values('103', 'Darkroom Workshop', 3, 'The Darkroom Workshop is an individualized course in darkroom techniques. Students may experiment with a variety of films, papers and processes. The course concentrates on refining the photographic process with individualized supervision in the darkroom. Prerequisite: PHOT 101 or permission of instructor. Course fee. Usually offered in summer session.', 44);
insert into class (num, name, credits, description, department_id) values('104', 'Color Photography I', 4, 'Color Photography is an introduction to exposure with color film and color darkroom techniques. Emphasis is on learning and applying darkroom processes and procedures leading to production of high-quality color photographs. Course includes 30 hours of lecture and 60 hours of lab per semester. Prerequisite: PHOT 102. Course fee.', 44);
insert into class (num, name, credits, description, department_id) values('105', 'Photojournalism', 3, 'Photojournalism is an introduction to the uses of photography as a journalistic tool. A portfolio based on a semester-long project is required. Course includes 30 hours of lecture and 30 hours of lab per semester. Prerequisite: PHOT 101 or permission of instructor. Course fee.', 44);
insert into class (num, name, credits, description, department_id) values('131', 'Digital Photography I', 3, 'Digital Photography I introduces students to the tools and processes of digital photography within a framework that emphasizes creative practice. Students learn fundamental skills necessary in the creation of digital photographic art, effective workflow management, and approaches to scanning and printing. Coursework includes lecture, studio work, and critique. This course is taught in the Mac lab studio using current software. Course includes 30 hours of lecture and 30 hours of lab per semester. Course fee.', 44);
insert into class (num, name, credits, description, department_id) values('191', 'Independent Study:Photography', 1, 'Course description not available at this time.', 44);
insert into class (num, name, credits, description, department_id) values('193', 'Independent Study: Photography', 3, 'Course description not available at this time.', 44);
insert into class (num, name, credits, description, department_id) values('202', 'Alternative Photographic Processes', 3, 'This course concentrates on the use of alternative processes, analogue and digital, in the creation of photographic images. Emphasis is on the development of a personal, self-expressive style through the use of traditional and non-traditional materials, media, equipment, and presentation strategies. Students taking PHOT 202 cannot receive credit for this course and PHOT 201. Course includes 30 hours of lecture and 30 hours of lab per semester. Prerequisites: PHOT 101 and PHOT 102. Course fee.', 44);
insert into class (num, name, credits, description, department_id) values('206', 'Studio Lighting', 3, 'This advanced course concentrates on the use of studio lighting procedures and equipment in the creation of portraiture and still life images in the studio and on location. Students taking PHOT 206 cannot receive credit for this course and PHOT 205 and/or PHOT 207. Course includes 30 hours of lecture and 30 hours of lab per semester. Prerequisites: PHOT 101 and PHOT 102. Course fee.', 44);
insert into class (num, name, credits, description, department_id) values('209', 'The History of Photography (GH)', 3, 'The History of Photography is a survey of the development of photography from its prehistory through today. The course includes the study of the interrelationships between photography and the other visual arts, the effects of changing technologies on the photographic image, and the contributions of major photographers and art movements, as well as historical perspectives. This course may require field trips.', 44);
insert into class (num, name, credits, description, department_id) values('231', 'Digital Photography II', 3, 'Digital Photography II continues the development introduced in Digital Photography I. Students learn in-depth processes of image manipulation to create complex photographic imagery culminating in the production of a portfolio. Course work includes lecture, studio work and critique. This course is taught in the Mac lab studio using current software. Course includes 30 hours of lecture and 30 hours of lab per semester. Prerequisite: PHOT 131 or permission of instructor. Course fee.', 44);
insert into class (num, name, credits, description, department_id) values('101', 'Beginning Tennis', 1, 'This course involves instruction of the fundamental skills of tennis. Students are required to physically practice basic tennis skills and drills. Rules of play, game scoring, and the health benefits of tennis are presented. No previous tennis experience is required.', 45);
insert into class (num, name, credits, description, department_id) values('102', 'Intermediate Tennis', 1, 'This course involves instruction in intermediate skills of tennis. Students are required to physically practice these skills. Advanced game strategies, drills for improvement, individual analysis and technique correction are presented. Basic tennis skill proficiency is strongly advised.', 45);
insert into class (num, name, credits, description, department_id) values('104', 'Beginning Bowling', 1, 'This course involves instruction of beginner bowling skills. Students are required to physically practice fundamental bowling skills. The selection and care of bowling equipment, game scoring, and bowling etiquette are presented. No previous bowling experience is required. Course fee.', 45);
insert into class (num, name, credits, description, department_id) values('130', 'Hiking', 1, 'This course is an introduction to hiking techniques. Students are required to physically complete hikes at trail locations in Harford, Baltimore, and Cecil Counties. Map-reading skills, hike preparation considerations and the physical benefits of hiking are presented. Students must provide their own transportation to trail locations. While no previous hiking experience is required, the physical ability to complete moderately difficult terrains is strongly advised.', 45);
insert into class (num, name, credits, description, department_id) values('131', 'Beginning Badminton', 1, 'This course involves instruction of beginner badminton skills. Students are required to physically practice fundamental skills of badminton, including basic stroke orientation, strategy, rules, and scoring. No previous badminton experience is required.', 45);
insert into class (num, name, credits, description, department_id) values('133', 'Aqua Yoga', 1, 'Aqua yoga combines the benefits of traditional yoga and continuous water exercise. This course requires students to physically participate in pool exercise designed to improve muscular endurance and flexibility. Swimming skills are not required. Course fee.', 45);
insert into class (num, name, credits, description, department_id) values('134', 'Cross Training for Fitness', 1, 'Cross Training exercise employs two or more training methods in one workout session to develop cardiorespiratory and muscular endurance. This course requires students to physically participate in a group exercise program that uses a variety of equipment, including barbells, steps, rowers, jump ropes, and resistance bands. Course fee.', 45);
insert into class (num, name, credits, description, department_id) values('135', 'Indoor Cycling', 1, 'Indoor cycling is performed on specialized stationary bikes that simulate an outdoor riding experience. Students are required to physically participate in an indoor group cycling program that includes off- bike muscular endurance and flexibility exercises to enhance cycling performance. Course fee.', 45);
insert into class (num, name, credits, description, department_id) values('136', 'Cardiovascular Fitness', 1, 'This course presents principles of cardiorespiratory endurance. Students are required to physically participate in a cardiorespiratory endurance program using stationary equipment, such as treadmills, cross trainers, and bikes. Course Fee.', 45);
insert into class (num, name, credits, description, department_id) values('137', 'Circuit Training', 1, 'Circuit training is a method of total body conditioning that involves movement from one exercise to another, using different pieces of equipment. Students are required to physically participate in a circuit training program designed to improve muscular endurance and cardiorespiratory endurance. Course fee.', 45);
insert into class (num, name, credits, description, department_id) values('138', 'Jogging', 1, 'This course presents principles and techniques of jogging. Students are required to physically participate in a jogging program. While no previous jogging experience is required, the physical ability to sustain high-impact cardiorespiratory exercise is strongly advised.', 45);
insert into class (num, name, credits, description, department_id) values('147', 'Mountain Biking', 1, 'This course presents principles and techniques of mountain bike riding. Students are required to physically complete bike rides at trail locations in Harford, Baltimore, and Cecil Counties. The mechanical, physical, and safety aspects of trail riding are presented. Students must provide their own transportation to trail locations, a mountain bike and a helmet. An understanding of basic mountain biking skills is strongly advised. Students should be physically able to bike moderately difficult terrain. Course fee.', 45);
insert into class (num, name, credits, description, department_id) values('150', 'Aikido I', 1, 'Aikido is a form of self-defense that uses the principles of nonresistance in order to debilitate the strength of the opponent. Students are required to participate in paired partner practice to learn movements that respond to a given attack with a specific defense. No previous Aikido experience is required.', 45);
insert into class (num, name, credits, description, department_id) values('153', 'Karate', 1, 'Karate is a form of self-defense that uses the hands and feet to deliver and block blows. Students are required to physically practice basic self-defense skills, flexibility exercises, and mental focus techniques. No previous Karate experience is required.', 45);
insert into class (num, name, credits, description, department_id) values('154', 'Hatha Yoga', 1, 'Yoga promotes spiritual health and physical fitness. This course requires physical participation in a yoga exercise program designed to improve muscular endurance and flexibility, breath control, and mental concentration. No previous yoga experience is required.', 45);
insert into class (num, name, credits, description, department_id) values('155', 'Matwork Pilates', 1, 'Pilates involves a series of controlled movements designed to improve core muscle stability and strength. This course requires physical participation in a Pilates mat exercise program. No previous Pilates experience is required.', 45);
insert into class (num, name, credits, description, department_id) values('169', 'Aqua Fitness', 1, 'This course presents principles and techniques of aqua exercise designed to improve physical fitness. Students are required to physically participate in group water exercise that uses a variety of resources, including kickboards and swim bars. Swimming skills are not required. Course fee.', 45);
insert into class (num, name, credits, description, department_id) values('171', 'Aqua Run I', 1, 'Aqua run is an exercise program that includes continuous deep water movement to develop cardiorespiratory and muscular endurance. Students are required to physically participate in water exercise. While swimming skills are not required, students should feel comfortable exercising in deep water. Flotation devices that assist deep water running are provided. Course fee.', 45);
insert into class (num, name, credits, description, department_id) values('178', 'Dance Movement', 1, 'This course presents creative dance techniques, improvisation, and choreography. Students are required to physically practice dance movement skills, including jazz, ballet, and modern dance. No previous dance training is required.', 45);
insert into class (num, name, credits, description, department_id) values('191', 'Independent Study: Physical Education', 1, 'This course provides an overview of the concepts of wellness and physical fitness. Topics include fitness, nutrition, stress management, holistic health, and chronic health issues.', 45);
insert into class (num, name, credits, description, department_id) values('228', 'Weight Training', 1, 'This course presents principles of weight training, with an emphasis on proper form and technique. Students are required to physically participate in a weight training program designed to increase muscular strength or muscular endurance. No previous weight lifting experience is required. Course fee.', 45);
insert into class (num, name, credits, description, department_id) values('229', 'Advanced Weight Training', 1, 'This course presents principles of advanced weight training, with an emphasis on proper form and technique. Students are required to physically participate in a weight training program designed to increase muscular strength or muscular endurance. Previous weight lifting experience is strongly advised. Course Fee.', 45);
insert into class (num, name, credits, description, department_id) values('230', 'Fitness Walking', 1, 'This course presents principles and techniques of a walking program. Students are required to physically participate in a walking program designed to improve cardiorespiratory endurance.', 45);
insert into class (num, name, credits, description, department_id) values('273', 'Cooperative Education III: Physical Education', 3, 'Course description not available at this time.', 45);
insert into class (num, name, credits, description, department_id) values('105', 'Physical Science I (GS)', 3, 'Physical Science I is a development of physical science for nonscience majors. Insight into the methods of scientific investigation is stressed. Topics include motion and force, energy and energy transfer, properties of matter, heat, electricity, magnetism, and light. Emphasis is on building process skills and content understanding using a "hands on" inquiry based teaching methodology. Insight into the structure, the beauty and the power of the physical sciences is stressed throughout the course. Course meets AAT (Associate of Arts in Teaching) degree requirements.', 46);
insert into class (num, name, credits, description, department_id) values('106', 'Physical Science Course Observations and Investigations: Matter (GL)', 1, 'An introductory laboratory course in the basic techniques of measurements and analysis of the motion of bodies, heat properties of bodies and the characteristics of waves. Usually offered in fall semester. Prerequisite or co-requisite: SCI 105. SCI 105 may also be taken concurrently. The course meets for a total of 30 laboratory hours per semester. Course fee.', 46);
insert into class (num, name, credits, description, department_id) values('107', 'Physical Science II', 3, 'Physical Science II is a development of physical science for nonscience majors. Insight into the methods of scientific investigation is stressed with emphasis on the electromagnetic spectrum, nuclear phenomena, the nature of solid matter, and the impact of science, computer and technology on twenty-first century humans. Forces in matter, moving charges, atomic models, crystal structure, chemical reactions and environmental consequences are studied. Usually offered in spring semester.', 46);
insert into class (num, name, credits, description, department_id) values('108', 'Physical Science Course Observations and Investigations: Energy', 1, 'This is an introductory laboratory course in the basic techniques of measurements and analysis of the electrical properties of bodies and the properties of atoms. Usually offered in spring semester. The course meets for a total of 30 laboratory hours per semester. Prerequisite or co-requisite: SCI 107. Course fee.', 46);
insert into class (num, name, credits, description, department_id) values('109', 'Introduction to Energy & Sustainability (GS)', 3, 'This course introduces students to the essential principles and fundamental concepts of energy necessary to build an energy literate society. Topics include the history of energy use, energy science and mechanics, electricity, sources of energy and use of energy, energy conservation and efficiency, environmental impacts, health effects, economics, policy, and future technology. Field experiences may be required; a reasonable alternative to the field trips will be available. Pre-requisite: Test into college level reading or successfully complete ENG 003; MATH 023 or MATH 024. This course meets for a total of 45 lecture hours. Course fee.', 46);
insert into class (num, name, credits, description, department_id) values('263', 'Special Topics:', 3, 'This couses is designed to serve as science, technology, engineering and mathematics (STEM) professional development for in-service K-12 teachers. Discipline-specific tracks (such as engineering, biology/chemistry, earth/environmental science) will focus on active learning of currently relevant topics and the development of best teaching and learning practices in STEM.', 46);
insert into class (num, name, credits, description, department_id) values('101', 'Introductory Physics I (GL)', 4, 'This course is for students requiring noncalculus based physics. It is a presentation of the fundamentals of physics emphasizing mechanics, heat and wave motion. Physics is treated as a living, expanding adventure that can turn you on to a more perceptive view of physical reality. Insight into the structure, the beauty and the importance of physics is achieved by study and discussion of the central ideas and principles of physics and their relation to the everyday environment. Course meets for 45 hours of lecture and 30 hours of laboratory and 15 hours of discussion per semester. Prerequisite: MATH 109 or equivalent (may be taken concurrently). Usually offered in fall semester.', 47);
insert into class (num, name, credits, description, department_id) values('102', 'Introductory Physics II (GL)', 4, 'This course is for students requiring noncalculus based physics, presenting fundamentals of physics including electromagnetism, relativity, structure of matter, atomic and nuclear physics. Course meets for 45 hours of lecture, 30 hours of laboratory and 15 hours of discussion per semester. Prerequisite: PHYS 101. Usually offered in spring semester.', 47);
insert into class (num, name, credits, description, department_id) values('200', 'General Physics I Lab (GL)', 1, 'This is an introductory laboratory course designed to accompany General Physics I (Physics 203) that explores basic physical concepts in vectors, laws of motion, force and energy; and principles of mechanics, collisions, linear and angular momentum, rotation and gravitation. Course meets for 45 hours per semester. Pre or co-requisite: PHYS 203', 47);
insert into class (num, name, credits, description, department_id) values('203', 'General Physics: Mechanics and Particle Dynamics (GS)', 3, 'This course is the first semester of a calculus-based general physics course sequence. Laws of motion, force and energy; and principles of mechanics, collisions, linear momentum, rotation and gravitation are studied and used for problem solving. Course meets for 45 hours of lecture and 15 hours of discussion/problem solving per semester. Prerequisite: MATH 203. Usually offered in spring semester.', 47);
insert into class (num, name, credits, description, department_id) values('204', 'General Physics: Vibrations, Waves, Heat, Electricity and Magnetism (GL)', 4, 'This second semester of a calculus-based general physics course sequence covers vibrations, waves and fluids; heat, kinetic theory and thermodynamics; electrostatics, circuits and magnetism. Course meets for 45 hours of lecture, 45 hours of laboratory, and 15 hours of discussion/problem solving per semester.Prerequisites: PHYS 203 and MATH 204. Usually offered in fall semester.', 47);
insert into class (num, name, credits, description, department_id) values('205', 'General Physics: Electrodynamics, Light Relativity and Modern Physics', 4, 'This third semester of a calculus-based general physics sequence covers electrodynamics, Maxwell''s equations, electromagnetic waves, geometrical optics, interference and diffraction, special theory of relativity, and modern physics. Course meets for 45 hours of lecture, 45 hours of laboratory, and 15 hours of discussion/problem solving per semester. Prerequisite: PHYS 204. Usually offered in spring semester.', 47);
insert into class (num, name, credits, description, department_id) values('101', 'American National Government (GB)', 3, 'Students participate in an analysis of American national politics. Topics include the Constitution, political parties, interest groups, Congress, the Presidency, the Judiciary and recent public policies. Special consideration is given to the individual''s relationship with the national government and to the factors influencing decision-making in the national government.', 48);
insert into class (num, name, credits, description, department_id) values('102', 'State and Local Government (GB)', 3, 'Students participate in analysis of state and local government, politics and policies. Topics include theory, intergovernmental regulations, state constitutions, political parties, interest groups, legislatures, executives, courts, subdivision governments, metropolitan politics and current issues. An effort is made to understand variables and pressures involved in state and local governmental decision-making, especially in Maryland and Harford County. Usually offered in the evening.', 48);
insert into class (num, name, credits, description, department_id) values('106', 'Introduction to Law (Same course as PL 101) (GB)', 3, 'This course introduces students to basic legal concepts, principles, and procedures. It is designed to provide the student with an understanding of the structure of the U.S. legal system including the role of the judicial, legislative, and executive branches; the history of law in the United States; the role of attorneys, law enforcement and other legal professionals; ethical and professional issues facing legal professionals; basic categories of law; litigation principles; and alternative dispute resolutions.', 48);
insert into class (num, name, credits, description, department_id) values('201', 'Introduction to International Relations (GB)', 3, 'This course has two major goals. One goal is to survey contemporary international issues so that students have a comprehensive understanding of issues in the Middle East, China, Russia, Africa and other areas of current interest. A second goal is to introduce students to the methods and objectives of foreign policy-making, the theory of the balance of power, the principles of international law and organization, and the development of regional integration such as in Europe. 3 credits. Corequisite: Eligibility for ENG 101', 48);
insert into class (num, name, credits, description, department_id) values('203', 'Comparative Politics and Governments', 3, 'This course introduces students to the comparative study of politics and governments. Following an overview of political systems, the instructor selects representative countries from both the developed and developing worlds and highlights issues including political culture, participation, government structures, and public policies in each. Using case studies, students engage in comparative political analysis of both historical processes and current issues facing countries domestically and internationally. 3 credits. Corequisite: Eligibility for ENG 101', 48);
insert into class (num, name, credits, description, department_id) values('204', 'Urban Government and Politics (GB) (D)', 3, 'Students participate in analyses of urban government and politics. The course examines the evolution of urban/metropolitan development, theory, and structure in the United States; identifies the key actors and addresses their roles both within the city and as liaisons with local, state and federal governments; and addresses the most pressing issues currently facing these jurisdictions, including: economic development, suburbanization, city infrastructure and finance, race relations and immigration, poverty, housing, law enforcement, and education. Prerequisite: ENG 012, ENG 018, or score on ACCUPLACER ACE qualifying student to take ENG 101.', 48);
insert into class (num, name, credits, description, department_id) values('263', 'Special Topics: Exploring U.S. Public Policy Making', 3, 'This course will enable students to increase their understanding of contemporary issues in international relations in a global context. It places emphasis on gaining critical perspectives on contemporary theory and practice--the goal is to develop the student''s ability to evaluate and explain contemporary issues from historical and theoretical perspectives in the disciplines of international relations. After a review of how nation-states interact in the international arean, we will travel to Europe and focus primarily on the two types of non-state actors (intergovernmental and nongovernmental) and the roles they play. We will visit and be briefed by representatives at the United Nations in New York, the European Union headquarters in Brussels, the International Court of Justice in The Hague and the Bretton Woods Project in London. This course is designed to address issues such as regionalization, globablization, international conflict, the future of democracy and others in an interdisciplinary way and provide a solid grounding in research methods in the social sciences. Students will gain a deep understanding of how international organizations affect the practice of world politics because they will experience it for themselves. A course fee of approximately $2500 covers all travel expenses. Pre-requisite: Instructor permission.', 48);
insert into class (num, name, credits, description, department_id) values('281', 'Political Science Internship', 1, 'Students work as interns in governmental agencies, in elected officials'' offices or on political campaigns. The purpose of the internship includes providing students with practical experiences in politics and government, and giving to students the opportunity to determine if they desire a career in government. A wide variety of field placements exists. Prerequisite: permission of instructor.', 48);
insert into class (num, name, credits, description, department_id) values('282', 'Political Science Internship', 2, 'Students work as interns in governmental agencies, in elected officials'' offices or on political campaigns. The purpose of the internship includes providing students with practical experiences in politics and government, and giving to students the opportunity to determine if they desire a career in government. A wide variety of field placements exists. Prerequisite: permission of instructor.', 48);
insert into class (num, name, credits, description, department_id) values('283', 'Political Science Internship', 3, 'Students work as interns in governmental agencies, in elected officials'' offices or on political campaigns. The purpose of the internship includes providing students with practical experiences in politics and government, and giving to students the opportunity to determine if they desire a career in government. A wide variety of field placements exists. Prerequisite: permission of instructor.', 48);
insert into class (num, name, credits, description, department_id) values('284', 'Political Science Internship', 4, 'Students work as interns in governmental agencies, in elected officials'' offices or on political campaigns. The purpose of the internship includes providing students with practical experiences in politics and government, and giving to students the opportunity to determine if they desire a career in government. A wide variety of field placements exists. Prerequisite: permission of instructor.', 48);
insert into class (num, name, credits, description, department_id) values('285', 'Political Science Internship', 5, 'Students work as interns in governmental agencies, in elected officials'' offices or on political campaigns. The purpose of the internship includes providing students with practical experiences in politics and government, and giving to students the opportunity to determine if they desire a career in government. A wide variety of field placements exists. Prerequisite: permission of instructor.', 48);
insert into class (num, name, credits, description, department_id) values('286', 'Political Science Internship', 6, 'Students work as interns in governmental agencies, in elected officials'' offices or on political campaigns. The purpose of the internship includes providing students with practical experiences in politics and government, and giving to students the opportunity to determine if they desire a career in government. A wide variety of field placements exists. Prerequisite: permission of instructor.', 48);
insert into class (num, name, credits, description, department_id) values('118', 'Maternal-Child Health for Practical Nursing', 4, 'Students use the nursing process, within the scope of practical nursing, to identify physiological and psychological stresses interfering with the well-being of the childbearing woman, the newborn infant, and children. The emphasis of this course is on providing basic nursing care to assist the client to adapt to or eliminate stresses which interfere with maternal and child health. Five weeks; summer term only. Course includes 30 hours of lecture and 70 hours in a clinical setting per 5-week summer term. Prerequisites: NURS 105, NURS 107, NURS 112. Current CPR Certification for Healthcare Providers. Corequisites: PN 121, PN 122. Course fee.', 49);
insert into class (num, name, credits, description, department_id) values('121', 'Medical-Surgical Nursing for Practical Nursing', 5, 'Within the scope of practical nursing, students use the nursing process in a structured setting to provide nursing care for clients who are experiencing acute or chronic physiological stressors throughout the life span and help promote physiological and psychological well-being. Ten weeks; summer term only. Course includes 50 hours of lecture and 70 hours in a clinical setting per 10-week summer term. Prerequisites: NURS 105, NURS 107, NURS 112, Current CPR Certification for Healthcare Providers. Corequisite: PN 118 and PN 122. Course fee.', 49);
insert into class (num, name, credits, description, department_id) values('122', 'Practical Nursing Issues', 0, 'This course provides students with information about issues and trends that influence their careers in practical nursing. Content includes the development of nursing, legal and ethical issues affecting nursing, and career opportunities and challenges. Five weeks; summer term only. Course includes 5 hours of lecture per 5-week summer term. Prerequisite: NURS 105, NURS 107, NURS 112. Corequisites: PN 118 and PN 121.', 49);
insert into class (num, name, credits, description, department_id) values('101', 'General Psychology (GB)', 3, 'A broad spectrum of research and theoretical concepts are presented to provide a balanced understanding of human behavior. Topics include the biological basis of behavior, human development, personality, health and wellness, learning and memory, social diversity, abnormal behavior and therapy. Prerequisites: a qualifying score on the English assessment or ENG 012 or ENG 018 or ENG 101.', 50);
insert into class (num, name, credits, description, department_id) values('105', 'Human Relations', 3, 'This course is a learning experience designed to provide students with skills necessary to develop a sensitivity to others, to become more effective listeners, and to convey awareness, understanding and patience. Students may become more effective in dealing with many different kinds of people in groups, organizations and in the community. This course is especially recommended for persons in business, technical and service career fields that require an effective skill level in interpersonal relations and communications.', 50);
insert into class (num, name, credits, description, department_id) values('202', 'Child Psychology', 3, 'This course studies the developing person from conception through late childhood. Current research and applications are used to explain biosocial, psychosocial and cognitive development. The importance of specific environmental contexts in development is emphasized. Topics include bonding and attachment, language development, abuse and neglect, parenting, gender role development, mental retardation and giftedness, peer relationships, and moral development. Prerequisite: PSY 101.', 50);
insert into class (num, name, credits, description, department_id) values('204', 'Abnormal Psychology', 3, 'This course presents the study of abnormal behavior including anxiety, mood disorders, schizophrenia, substance abuse disorder, and other DSM categories. Topics include diagnosis, classification, causes, prevention and treatment modalities with emphasis on eclectic, biological, and cognitive models, as well as cultural influences, community needs and resources. Prerequisite: PSY 101.', 50);
insert into class (num, name, credits, description, department_id) values('205', 'Psychology of Women (D)', 3, 'This course is a survey of the socialization, gender role development, mental health, special concerns and life span changes of women. The relationship of psychology to the position and roles of women is also examined from a cross-cultural perspective. Usually offered in spring semester.', 50);
insert into class (num, name, credits, description, department_id) values('207', 'Educational Psychology', 3, 'Educational Psychology focuses on the learning process and related ideas such as development, individual differences, cognition, effective learning environments, motivation and exceptionalities. Emphasis is placed on studying the development of effective teaching-learning relationships in the American school environment. Connections among a variety of disciplines are stressed, as well as links to the real world beyond the classroom. Prerequisite: PSY 101.', 50);
insert into class (num, name, credits, description, department_id) values('208', 'Alcohol/Drugs: Impact on Behavior', 3, 'This course studies the physiological and psychological effects of alcohol and depressants, psychoactive drugs, stimulants and hallucinogens on the user and abuser. Topics include the effects of drugs taken in combination, drug classification, absorption, distribution, metabolism, half-life, tolerance, cross-tolerance and elimination. Prerequisite: PSY 101. Usually offered in spring semester.', 50);
insert into class (num, name, credits, description, department_id) values('209', 'Social Psychology (D)', 3, 'This course incorporates psychological and sociological theory and research as applied to contemporary group situations. A wide range of issues concerning human experience in group settings is explored, including interpersonal attraction, gender roles and sexism, cross-cultural and within cultural differences, attitude formation based on group membership, prejudice, conflict, power and aggression between groups, pro-social behavior and group conformity. Prerequisite: PSY 101.', 50);
insert into class (num, name, credits, description, department_id) values('210', 'Industrial and Organizational Psychology', 3, 'Using the contributions from researchers in psychology, sociology and other behavioral sciences, this course seeks to explain and understand behavior in the workplace. A broad range of issues is explored, including organizational behavior, motivation, learning, attitudes and job satisfaction, socialization, power, stress and group structures and effectiveness. The course is designed for behavioral science majors, and those in the workplace who want to update their management, supervisory or interpersonal skills. Usually offered in spring semester.', 50);
insert into class (num, name, credits, description, department_id) values('212', 'The Helping Relationship', 3, 'This is an introductory counseling skills course that emphasizes developing skills and techniques to facilitate the helping process, through an understanding of helping theory and the dynamics of the helping relationship. Helper self development will be achieved via in-class exercises and the use of a pseudo-client. Prerequisite: PSY 101 or permission of instructor. Usually offered in fall semester.', 50);
insert into class (num, name, credits, description, department_id) values('214', 'Human Development Across the Life Span', 3, 'This course studies the developing person across the life span from conception to death. Numerous theoretical perspectives are applied to studying biological, cognitive, psychosocial change. Emphasis is given to the role of genetic influences as well as the specific environmental contexts in which development occurs. Prerequisite: PSY 101.', 50);
insert into class (num, name, credits, description, department_id) values('216', 'Adolescent Psychology', 3, 'This course presents the period of adolescence as a distinct stage in the lifespan, with its own unique biological, psychosocial, and cognitive issues. Current theoretical perspectives and research findings are used to explain and explore such topics as the challenges of puberty, identity development, risk taking behavior, intimate relationships, and the impact of cultural variables on adolescent development. Prerequisite: PSY 101. Usually offered in the Spring semester.', 50);
insert into class (num, name, credits, description, department_id) values('263', 'Special Topics: Survey of Biopsychology', 3, 'Special Topics: Survey of Biopsychology Biopsychology is a rapidly growing branch of psychology that studies the relationship between the structure and functioning of the nervous system and behavior. This course will introduce students to the biological bases of behavior by exploring research findings of biopsychologists in explaining such behaviors as wakefulness and sleep, learning and memory, emotions and stress, psychological disorders such as schizophrenia and depression, and reproductive functioning. New information in this field is rapidly increasing; as students examine the current level of knowledge in this field and develop a framework of biopsychological principles, they will become prepared to incorporate the future knowledge that is sure to follow.', 50);
insert into class (num, name, credits, description, department_id) values('273', 'Cooperative Education III: Psychology', 3, 'Cooperative Education experiences are work-based learning experiences with an employer for a specific period of time. The experience may be paid and must be related to the career and specific curriculum in which the student is enrolled. It is an opportunity for the student to supplement/integrate classroom learning with learning from a related work setting. A student registers for one to four credits of Cooperative Education in the curriculum in which he/she is enrolled. Prerequisite: approval of program coordinator.', 50);
insert into class (num, name, credits, description, department_id) values('274', 'Coop Educ IV Psychology', 4, 'Cooperative Education experiences are work-based learning experiences with an employer for a specific period of time. The experiences may be paid and must be related to the career and specific curriculum in which the student is enrolled. It is an opportunity for the student to supplement/integrate classroom learning with learning from a related work setting. A student registers for one to four credits of Cooperative Education in the curriculum in which he/she is enrolled. Prerequisite: approval of program coordinator.', 50);
insert into class (num, name, credits, description, department_id) values('207', 'Literature and Religious Thought of the Old Testament (GH)', 3, 'This course is a survey and analysis of the literature and religion of the Old Testament within the context of the ancient Near East. The course is an objective academic study and is not faith-based.', 51);
insert into class (num, name, credits, description, department_id) values('208', 'Literature and Religious Thought of the New Testament (GH)', 3, 'This course is a survey and academic analysis of The New Testament, related early Christian literature, and the historical, archaeological, and cultural contexts that influenced these documents. The course is an objective academic study and is not faith-based.', 51);
insert into class (num, name, credits, description, department_id) values('210', 'Comparative Religion (GH) (D)', 3, 'This course is a comparative overview of the most prominent living religions. Each religion is approached from the view of the religion''s history, culture, beliefs, rituals, and theologies.', 51);
insert into class (num, name, credits, description, department_id) values('101', 'Introduction to Sociology (GB) (D)', 3, 'This course is the scientific study of society. Detailed consideration is given to culture, social control and deviation, social groups, social instruction, social stratification, ethnic minorities, demography, the community, social change and collective behavior.', 52);
insert into class (num, name, credits, description, department_id) values('102', 'Social Problems (GB) (D)', 3, 'This course places major emphasis on a variety of contemporary American and world social and cultural issues -- ranging from social justice issues and diversity (gender, race, and social class) to criminal justice issues and violence to global concerns such as the ecological system, war, and terrorism. Analysis of multiple causation and past historical origins are connected to contemporary problems.', 52);
insert into class (num, name, credits, description, department_id) values('200', 'Introduction to Social Work', 3, 'The study of social work as a professional endeavor is the focus of this class. Students explore the scope of social welfare in connection with social change, social control and the relationship between services and clients. This course is of value to sociology and psychology majors who intend to work as mental health aides or in other allied areas. Prerequisite: SOC 101, PSY 101 recommended.', 52);
insert into class (num, name, credits, description, department_id) values('201', 'Marriage and the Family (GB) (D)', 3, 'This course provides analysis of the social institution of the family through theory and research in the field. Emphasis is placed on the social organization of the family in its structure and its function, including detailed consideration of historical cultural factors, social class elements, premarital matters, marriage adjustment and the family life cycle.', 52);
insert into class (num, name, credits, description, department_id) values('213', 'Criminology (same course as CJ 213)', 3, 'This course provides a sociological study of the causes of crime and the relationships between criminal behavior and various social factors such as age, sex, race, religion, socioeconomic status, etc. Included also are studies of crime rates, white-collar crime and victimless crimes. Prerequisite: SOC 101.', 52);
insert into class (num, name, credits, description, department_id) values('214', 'Juvenile Delinquency', 3, 'The student is introduced to the nature and extent of juvenile delinquency. Emphasis is placed on the causative factors involved and methods of control and prevention. Special attention is given to the relationship between delinquency and the social structure.', 52);
insert into class (num, name, credits, description, department_id) values('263', 'Special Topics: Race, Class and Gender', 3, 'This course will examine the nature and interrelationships of race, class, and gender in American Society. By using a sociological framework for understanding race, class, and gender and how they intersect with each other and in social structures, we will analyze and interpret contemporary social problems. In addition, we will use this framework to examine the racial, class, and gender dimensions of the Civil Rights Movement and its impact on contemporary social issues.', 52);
insert into class (num, name, credits, description, department_id) values('273', 'Coop Educ III Sociology', 3, 'Cooperative Education experiences are work-based learning experiences with an employer for a specific period of time. The experience may be paid and must be related to the career and specific curriculum in which the student is enrolled. It is an opportunity for the student to supplement/integrate classroom learning with learning from a related work setting. A student registers for one to four credits of Cooperative Education in the curriculum in which he/she is enrolled. Prerequisite:approval of program coordinator.', 52);
insert into class (num, name, credits, description, department_id) values('101', 'Elementary Spanish I', 3, 'This course develops communicative proficiency in Spanish at the elementary level. Students also gain insights into Spanish-speaking cultures. It is primarily designed for students who have never studied Spanish. No prerequisite. Usually offered in fall semester.', 53);
insert into class (num, name, credits, description, department_id) values('102', 'Elementary Spanish II', 3, 'This course continues to develop communicative proficiency in Spanish at the elementary level. Students also explore aspects of the Spanish culture. Prerequisite: SPAN 101 or equivalent, or permission of instructor. Usually offered in spring semester.', 53);
insert into class (num, name, credits, description, department_id) values('201', 'Intermediate Spanish I', 3, 'This course continues to develop language skills in Spanish at an advanced level. Cultural materials are also integrated into course content and activities. Prerequisite: SPAN 102 or equivalent, or permission of instructor. Usually offered in fall semester.', 53);
insert into class (num, name, credits, description, department_id) values('202', 'Intermediate Spanish II', 3, 'This course emphasizes the continued refinement and development of language skills in Spanish at an advanced level. Students also discuss cultural materials. Prerequisite: SPAN 201 or equivalent, or permission of instructor. Usually offered in spring semester.', 53);
insert into class (num, name, credits, description, department_id) values('203', 'Survey of Spanish Literature I (GH)', 3, 'This course is a study of Spanish culture, civilization and literature from the Middle Ages to the 1800s. All work is in Spanish. Prerequisite: SPAN 202 or equivalent, or permission of instructor. Usually offered in fall semester.', 53);
insert into class (num, name, credits, description, department_id) values('204', 'Survey of Spanish Literature II', 3, 'This course is a study of Spanish culture, civilization and literature from 1800 to the present. All work is in Spanish. Prerequisite: SPAN 202 or equivalent, or permission of instructor. Usually offered in spring semester.', 53);
insert into class (num, name, credits, description, department_id) values('205', 'Advanced Conversation (D)', 3, 'This course develops oral proficiency of students who are in need of applying their grammatical knowledge to real life situation discourse. Grammatical concepts from elementary and intermediate classes are practiced and expanded for more extensive dialogue. Subject matter stems from themes relative to language, cultural, global, and political issues. Students apply their speaking skills by engaging in conversations, panel discussions, short presentations, interviews, and group work. Prerequisite: SPAN 202 or equivalent, or permission of instructor. Usually offered in fall semester.', 53);
insert into class (num, name, credits, description, department_id) values('015', 'Personal & Academic Dev', 0, 'This course is designed to help students identify and develop their career/life and academic goals. Students will clarify their interests and skills while developing effective academic and personal goal-setting techniques. This course also focuses on setting and achieving short-term academic goals, preparing for success in college through effective study skill techniques, and learning to accept responsibility for one''s behavior. May not be used to meet graduation requirements.', 54);
insert into class (num, name, credits, description, department_id) values('103', 'Career and Life Planning', 3, 'This course is designed to assist each student to become more aware of the processes of career and life planning and their relationship to interests, values, abilities and goals. It prepares the student to establish, change or confirm career goals through investigation and integration of the theory of the developmental process of career decision-making, self-analysis and a survey of career information. Course fee.', 54);
insert into class (num, name, credits, description, department_id) values('110', 'Success in College and Beyond', 1, 'This course is designed to help students develop and refine skills necessary for success in college and in life. While encouraging students to take responsibility for their behavior, it provides practice in a variety of strategies that can lead students to greater academic, professional, and personal success. Students examine study skill strategies, creative and critical thinking, and personal self-management. In addition, students are encouraged to explore and change self-defeating behaviors which may diminish their ability to succeed. Permission of instructor required. Course fee.', 54);
insert into class (num, name, credits, description, department_id) values('111', 'Personalized Career Exploration', 1, 'This course provides individualized assistance to students in developing skills, competencies, and knowledge essential to career exploration and decision making. Students fulfill a learning contract based on needs for self-assessment and occupational research. Minimum of five hours with instructor and twenty hours of lab and research assignments. Permission of instructor required. Course fee.', 54);
insert into class (num, name, credits, description, department_id) values('112', 'Job Search Techniques', 1, 'This course provides assistance to students in developing skills, competencies and knowledge essential in securing employment. Students learn the use of technology in job searching, the writing of effective resumes and cover letters and necessary interviewing skills. Permission of instructor required.', 54);
insert into class (num, name, credits, description, department_id) values('101', 'Introduction to Theatre (GH)', 3, 'This course introduces students to the basic elements of theatrical performance and the components that make up theatre production, including understanding the roles of the actor, director, playwright, and designer. Students explore the varied methods of presentation, concepts, vocabulary, and the range of techniques and experiences involved in a production. Attendance at and critical evaluation of theatrical performances and participation on a performance crew may be required.', 55);
insert into class (num, name, credits, description, department_id) values('102', 'Acting I', 3, 'This course introduces students to basic acting skills, including exercises in characterization, relaxation and concentration, verbal and nonverbal communication, and expression. Students are required to attend or participate in productions. Course includes 30 hours of lecture and 30 hours of studio per semester. This course may require field trips.', 55);
insert into class (num, name, credits, description, department_id) values('103', 'Acting II', 3, 'This course includes the study of textural analysis, scene study, and the process of developing characterization in different historical styles of performance. Students are required to attend or participate in productions. Course includes 30 hours of lecture and 30 hours of studio per semester. This course may require field trips. Prerequisite: THEA 102.', 55);
insert into class (num, name, credits, description, department_id) values('104', 'Stagecraft I', 3, 'This course is a practical study of basic technical production with emphasis in scenic construction and lighting techniques. Topics include scenic design, construction, rigging, painting, and the handling of lighting instruments. This course consists of lecture as well as shop hours. Students are required to participate as technical crew in departmental productions. Course includes 30 hours of lecture and 30 hours of studio per semester. Course fee.', 55);
insert into class (num, name, credits, description, department_id) values('105', 'Stagecraft II', 3, 'This course continues the study of technical production needs of any theatrical production with an evaluation of the theatre production and an emphasis on the supervision of running crews for preparation and performance. This course consists of lecture as well as shop hours. Students are required to participate as technical crew in departmental productions. Course includes 30 hours of lecture and 30 hours of studio per semester. Prerequisite: THEA 104. Course fee.', 55);
insert into class (num, name, credits, description, department_id) values('106', 'Script Analysis: From Page to Stage', 3, 'This course focuses on the analysis of dramatic texts as plans for public performance. Students practice reading, researching, and planning productions based on a variety of important international plays. Attendance at and critical evaluations of performances and participation in performance projects are required. Course fee.', 55);
insert into class (num, name, credits, description, department_id) values('191', 'Independent Study: Theatre', 1, 'Course description not available at this time.', 55);
insert into class (num, name, credits, description, department_id) values('193', 'Independent Study: Theatre', 3, 'Course description not available at this time.', 55);
insert into class (num, name, credits, description, department_id) values('201', 'Fundamentals of Play Directing', 3, 'This course introduces students to the basic techniques, principles, and genres of directing a theatrical production. Topics include the director''s role, composition, script analysis, movement and rhythm, production preparation, and procedures. At the conclusion of the course, the student prepares a production for performance. Additional time outside of class for rehearsals is required. Prerequisites: THEA 101 and THEA 104.', 55);
insert into class (num, name, credits, description, department_id) values('202', 'Scene Design', 3, 'This course introduces theater forms and spaces through the various design elements and locales of a scenic environment. Students study the principles and techniques of scenic design as it pertains predominately to the stage, as well as television and film. Course includes 30 hours of lecture and 30 hours of studio per semester. This course may require field trips. Prerequisites: THEA 104. Course fee.', 55);
insert into class (num, name, credits, description, department_id) values('204', 'Costuming', 3, 'This course introduces the basic principles of costume design and construction. Students explore costume patterning, construction methods, draping, sewing technique, equipment usage, and skills necessary to the entire costuming process. Students are expected to serve on a costume crew for one production during the semester. Course includes 30 hours of lecture and 30 hours of studio per semester. Prerequisites: THEA 101 and THEA 279. Course fee.', 55);
insert into class (num, name, credits, description, department_id) values('205', 'Stagecraft II', 3, 'This course continues the study of technical production needs of any theatrical production with an evaluation of the theatre production and an emphasis on the supervision of running crews for preparation and performance. This course consists of lecture as well as shop hours. Students are required to participate as technical crew in departmental productions. Course includes 30 hours of lecture and 30 hours of studio per semester. Prerequisite: THEA 104. Course fee.', 55);
insert into class (num, name, credits, description, department_id) values('221', 'Vocal Performance for the Stage', 3, 'This course includes the study of voice production with extensive exercises in developing a wide range of controls in pitch, volume, diction, and quality to meet the standards of acting and media. Through a highly technical phonetic approach, students learn how to reduce their own and produce other American speech regionalisms and cultural accents. Course includes 30 hours of lecture and 30 hours of studio per semester. Prerequisites: THEA 102. Course fee.', 55);
insert into class (num, name, credits, description, department_id) values('222', 'Movement for the Actor', 3, 'This course introduces physical techniques that help develop a movement vocabulary for the actor. Starting with an examination of the body, the student applies the basic movement vocabulary and terminology of ballet, modern and jazz styles, mime, neutral mask, physical comedy (falls, lifts, timing, partnering), and scripted scenes with a strong physical component. This course may require field trips. Course includes 30 hours of lecture and 30 hours of studio per semester. Prerequisites: THEA 101 and THEA 102.', 55);
insert into class (num, name, credits, description, department_id) values('223', 'Makeup for the Performer', 3, 'This course is a studio course in which students develop an understanding of the art of traditional makeup application predominately for the stage as well as for television and film. Through the use of cosmetics and prosthetics, students learn to execute corrective, character, and age makeup. Students are expected to serve on a makeup crew for one production during the semester. Course includes 30 hours of lecture and 30 hours of studio per semester. Prerequisites: THEA 101 and THEA 279. Course fee.', 55);
insert into class (num, name, credits, description, department_id) values('273', 'Cooperative Education III Theatre', 3, 'The Cooperative Education Program provides students with a planned and supervised learning environment allowing them to apply classroom learning to the world of work. The program enables students to become better acquainted with both theory and practice in their chosen career field. 37.5 hours per credit (112 hours per 3 credit course). Prerequisites: 12 completed hours in Theatre and permission of instructor.', 55);
insert into class (num, name, credits, description, department_id) values('279', 'Theatre Workshop I', 1, 'This course is a practical application of classroom theory and technique demonstrated through crew and/or cast participation in a specific theatrical production to be presented on campus. Students elect or are assigned a task and work as a crew or cast member. Acting and/or dancing in a production is by audition only. Credit is awarded upon satisfactory completion of the task or role. Students are required to participate in productions. Prerequisite: THEA 104.', 55);
insert into class (num, name, credits, description, department_id) values('280', 'Theatre Workshop II', 1, 'This course is a practical application of classroom theory and technique demonstrated through crew and/or cast participation in a specific theatrical production to be presented on campus. Students elect or are assigned a task and work as a crew or cast member. Acting and/or dancing in a production is by audition only. Credit is awarded upon satisfactory completion of the task or role. Students are required to participate in productions. Prerequisite: THEA 104.', 55);
insert into class (num, name, credits, description, department_id) values('281', 'Theatre Workshop III', 1, 'This course is a practical application of classroom theory and technique demonstrated through crew and/or cast participation in a specific theatrical production to be presented on campus. Students elect or are assigned a task and work as a crew or cast member. Acting and/or dancing in a production is by audition only. Credit is awarded upon satisfactory completion of the task or role. Students are required to participate in productions. Prerequisite: THEA 104.', 55);
insert into class (num, name, credits, description, department_id) values('201', 'Visual and Performing Arts Survey (GH)', 3, 'This course introduces students to the areas of visual arts, dance, music and theater through an exploration of representative works. This experience enhances self-expression and provides a better understanding of the human experience. This course may require field trips.', 56);
